package ui

import (
	"fmt"
	"strings"
	"time"

	"github.com/kastheco/kasmos/keys"
	"github.com/kastheco/kasmos/session"

	"github.com/charmbracelet/lipgloss"
)

var keyStyle = lipgloss.NewStyle().Foreground(ColorSubtle)

var descStyle = lipgloss.NewStyle().Foreground(ColorMuted)

var sepStyle = lipgloss.NewStyle().Foreground(ColorOverlay)

var actionGroupStyle = lipgloss.NewStyle().Foreground(ColorRose)

package overlay

import (
	"bytes"
	"regexp"
	"strings"

	"github.com/charmbracelet/lipgloss"
	"github.com/mattn/go-runewidth"
	"github.com/muesli/ansi"
	"github.com/muesli/reflow/truncate"
	"github.com/muesli/termenv"
)

// Most of this code is modified from https://github.com/charmbracelet/lipgloss/pull/102

// Pre-compiled regexes for ANSI color code replacement in PlaceOverlay.
var (
	bgColorRegex     = regexp.MustCompile(`\x1b\[48;[25];[0-9;]+m`)
	fgColorRegex     = regexp.MustCompile(`\x1b\[38;[25];[0-9;]+m`)

package overlay

import (
	tea "github.com/charmbracelet/bubbletea"
	"github.com/charmbracelet/lipgloss"
)

// TextOverlay represents a text screen overlay
type TextOverlay struct {
	// Whether the overlay has been dismissed
	Dismissed bool
	// Callback function to be called when the overlay is dismissed
	OnDismiss func()
	// Content to display in the overlay
	content string

	width int
}

// NewTextOverlay creates a new text screen overlay with the given title and content

package session

import (
	"fmt"
	"path/filepath"
	"time"

	"github.com/kastheco/kasmos/session/git"
	"github.com/kastheco/kasmos/session/tmux"
)

type Status int

const (
	// Running is the status when the instance is running and claude is working.
	Running Status = iota
	// Ready is if the claude instance is ready to be interacted with (waiting for user input).
	Ready
	// Loading is if the instance is loading (if we are starting it up or something).
	Loading

// appConfig stores persistent application configuration
appConfig *config.Config
// appState stores persistent application state like seen help screens
appState config.AppState

// -- State --

// allInstances stores every instance across all repos (master list)
allInstances []*session.Instance

// state is the current discrete state of the application
state state
// seenNotified tracks an instance whose Notified flag was visible when selected.
// The flag is cleared when the user navigates away, not immediately on focus.
seenNotified *session.Instance

// instanceFinalizers stores per-instance finalizers keyed by instance pointer.
// Each finalizer registers the repo name after the instance has started.
// Supports concurrent batch spawns (e.g. wave tasks) where multiple
// instances start in parallel. Lazily initialized via addInstanceFinalizer.
